import React, {useState, useEffect} from 'react';
import storage from "local-storage-fallback";
import { ThemeProvider, createGlobalStyle } from "styled-components";

const GlobalStyle = createGlobalStyle`
body {
  background-color: ${props => (props.theme.mode === "dark" ? "#111" : "#EEE")};
  color: ${props => (props.theme.mode === "dark" ? "#EEE" : "#111")};
}
`;

//
import { Switch, Route, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import './App.css';

import HomePage from './pages/homepage/homepage.component';
import ShopPage from './pages/shop/shop.component';
import SignInAndSignUpPage from './pages/sign-in-and-sign-up/sign-in-and-sign-up.component';
import CheckoutPage from './pages/checkout/checkout.component';

import Header from './components/header/header.component';

import { auth, createUserProfileDocument } from './firebase/firebase.utils';

import { setCurrentUser } from './redux/user/user.actions';
import { selectCurrentUser } from './redux/user/user.selectors';

class App extends React.Component {
  unsubscribeFromAuth = null;

  componentDidMount() {
    const [theme, setTheme] = useState(getInitialTheme);
    useEffect(() => {
    storage.setItem("theme", JSON.stringify(theme));
    }, [theme]);

    function getInitialTheme() {
    const savedTheme = storage.getItem("theme");

    return savedTheme ? JSON.parse(savedTheme) : { mode: "light" };
    }
    //
    const { setCurrentUser } = this.props;
    // On cree un utilisateur et on snapshot ses informations dans notre state
    this.unsubscribeFromAuth = auth.onAuthStateChanged(async userAuth => {
      if (userAuth) {
        const userRef = await createUserProfileDocument(userAuth);

        userRef.onSnapshot(snapShot => {
          setCurrentUser({
            id: snapShot.id,
            ...snapShot.data()
          });
        });
      }

      setCurrentUser(userAuth);
    });
  }

  componentWillUnmount() {
    this.unsubscribeFromAuth();
  }


  render() {
    return (
      <ThemeProvider theme={theme}>
        <GlobalStyle />
        <div>
          <Header />
          <button onClick={() => setTheme(theme.mode === "dark" ? { mode: "light" } : { mode: "dark" })}>
            Toggle Mode
          </button>
          <Switch>
            <Route exact path='/' component={HomePage} />
            <Route path='/shop' component={ShopPage} />
            <Route exact path='/checkout' component={CheckoutPage} />
            <Route
              exact
              path='/signin'
              render={() =>
                this.props.currentUser ? (
                  <Redirect to='/' />
                ) : (
                  <SignInAndSignUpPage />
                )
              }
            />
          </Switch>
        </div>
      </ThemeProvider>
    );
  }
}
//Recuperer le current user du reducer
const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser
});
//Appel à la fonction redux pour set l'utilisateur actuel
const mapDispatchToProps = dispatch => ({
  setCurrentUser: user => dispatch(setCurrentUser(user))
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(App);
