import React, { Component } from 'react';
import { Route } from 'react-router-dom';

import CollectionsOverview from '../../components/collections-overview/collections-overview.component';
import CollectionPage from '../collection/collection.component';
import Spinner from '../../components/spinner/spinner.component'

import { firestore, convertCollectionsSnapshotToMap } from '../../firebase/firebase.utils';
import { connect } from 'react-redux';
import { updateCollections } from '../../redux/shop/shop.actions';

const CollectionsOverviewWithSpinner = Spinner(CollectionsOverview);
const CollectionPageWithSpinner = Spinner(CollectionPage);

class ShopPage extends Component {
  state = {
    loading: true
  }

  unsubscribeFromSnapshot = null;

  componentDidMount() {
    const { updateCollections } = this.props;
    const collectionRef = firestore.collection('collections');
    //Cette fonction nous retourne tous les documents contenus dans Firebase -> Collections
    this.unsubscribeFromSnapshot = collectionRef.onSnapshot(async snapshot => {
      const collectionsMap = convertCollectionsSnapshotToMap(snapshot);
      updateCollections(collectionsMap);
      this.setState({ loading: false });
    })
  }

  render() { 
    const { match } = this.props;
    return ( 
      <div className='shop-page'>
        <Route exact path={`${match.path}`} render={ (props) => 
          <CollectionsOverviewWithSpinner isLoading={this.state.loading} {...props} /> } 
        />
        <Route path={`${match.path}/:collectionId`} render={ (props) => 
          <CollectionPageWithSpinner isLoading={this.state.loading} {...props} /> } 
        />
    </div>
     );
  }
}

const mapDispatchToProps = dispatch => ({
  updateCollections: collectionsMap => dispatch(updateCollections(collectionsMap))
})


export default connect (null, mapDispatchToProps)(ShopPage);
